mapper.update:
update orders
number = #{number}, status = #{status}, user_id = #{userId}, address_book_id = #{addressBookId}, order_time = #{orderTime}, checkout_time = #{checkoutTime}, pay_method = #{payMethod}, pay_status = #{payStatus}, amount = #{amount}, remark = #{remark}, phone = #{phone}, address = #{address}, user_name = #{userName}, consignee = #{consignee}, cancel_reason = #{cancelReason}, rejectio_reason = #{rejectionReason}, cancel_time = #{cancelTime}, estimated_delivery_time = #{estimatedDeliveryTime}, delivery_status = #{deliveryStatus}, delivery_time = #{deliveryTime}, pack_amount = #{packAmount}, tableware_number = #{tablewareNumber}, tableware_status = #{tablewareStatus}
where id = #{id}

mapper.select:
select count(amount) from orders

    status = 5
    and order_time >= #{begin}
    and order_time <= #{end}
