mapper.update:
update setmeal
category_id = #{categoryId}, description = #{description}, price = #{price}, status = #{status}, image = #{image}, name = #{name}, update_time = #{updateTime}, update_user = #{updateUser}
where id = #{id}

mapper.select:
select * from setmeal

    category_id = #{categoryId}
    name = #{name}
    status = #{status}

order by update_time desc
