mapper.update:
update setmeal
category_id = #{categoryId}, description = #{description}, price = #{price}, status = #{status}, image = #{image}, name = #{name}, update_time = #{updateTime}, update_user = #{updateUser}
where id = #{id}

mapper.select:
select * from setmeal

    category_id = #{categoryId}
    name = #{name}
    status = #{status}

order by update_time desc

select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}

select sd.name, sd.copies, d.image, d.description
    from setmeal_dish sd left join dish d on sd.dish_id = d.id
    where sd.setmeal_id = #{setmealId}

select count(id) from setmeal


        and status = #{status}


        and category_id = #{categoryId}
