mapper.update:
update shopping_cart set number = #{number}

    user_id = #{userId}
     and setmeal_id = #{setmealId}
     and dish_id = #{dishId}
     and dish_flavor = #{dishFlavor}

mapper.delete:
delete from shopping_cart

    user_id = #{userId}
    and dish_id = #{dishId}
    and setmeal_id = #{setmealId}
    and dishFlavor = #{dishFlavor}

mapper.select:
select * from shopping_cart

     and user_id = #{userId}
     and dish_flavor = #{dishFlavor}
     and dish_id = #{dishId}
     and setmeal_id = #{setmealId}
